mapper.select:

select * from (select rownum rn,board_no,id,title,write_date
	from(select board_no,id,title,write_date
		 from board
		 order by write_date desc)
		 )where rn<= #{pageNum} * #{amount}
		and rn > (#{pageNum} -1) * #{amount}


select * from (select rownum rn, board_no, id, title, write_date
	from (select board_no, id, title, write_date
		from board
		where category = #{category}
		order by write_date desc
	)
) where rn <= #{pageNum} * #{amount}
and rn > (#{pageNum} - 1) * #{amount}
